import React, { useState, useEffect, useContext, useCallback } from 'react';
import { TouchableOpacity, Button, SafeAreaView, StyleSheet, Text, TextInput, View, TouchableOpacityBase } from 'react-native';
import Icon from 'react-native-vector-icons/MaterialCommunityIcons';
import Coin from 'react-native-vector-icons/FontAwesome5';
import DatePicker from 'react-native-modern-datepicker';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { SocketContext } from './socket';
import axios from 'axios';
import Chatbox from './chatbox';

export default function Home(props) {
    const [date, setDate] = useState('');
    const [time, setTime] = useState('');
    const [pickerOpen, setPickerOpen] = useState(false);
    const [active, setActive] = useState(false);
    const [turnOnNotify, setTurnOnNotify] = useState(false);


    const socket = useContext(SocketContext);

    useEffect(() => {
        async function em() {
            // const id = await AsyncStorage.getItem("userId");
            // const token = await AsyncStorage.getItem("token");
            const id = sessionStorage.getItem("userId");
            const token = sessionStorage.getItem("token");

            socket.emit('auth', {
                userId: id,
                token: token
            });
        }
        em();

        socket.on('triggered', (room, alarmId) => {
        });

        socket.on('get-snooze', () => {
        })
        socket.on('get-dismiss', () => {
        })
        socket.on('snooze-begin', () => {
        })
        socket.on('dismiss-begin', () => {
        })
        socket.on('room-disconnect', () => {
        })
        return () => {
            socket.offAny();
        }
    }, [socket])

    //------------------------------------------

    function storeClick() {
        props.navigation.navigate('Store');
    }
    function alarmsClick() {
        alert('already in alarms page');
    }
    function profileClick() {
        alert('clicked on profile');
    }

    function createAlarm() {
        setPickerOpen(false);
        setTurnOnNotify(true);

        console.log()
    }
    function cancelAlarm() {
        setActive(false);
        setPickerOpen(false);
        setTurnOnNotify(false);
    }
    function turnOn() {
        setTurnOnNotify(true);
    }

    async function confirmCreateAlarm() {
        setActive(true);
        setPickerOpen(false);
        setTurnOnNotify(false);

        const alarmString = `${date} ${time}:00`;
        const eventTime = new Date(alarmString);
        eventTime.setHours(eventTime.getHours() - 4);
        const finalTime = eventTime.toISOString();

        //-------- For Mobile use ASYNC STORAGE ---------
        // const USERID = await AsyncStorage.getItem("userId");
        // const TOKEN = await AsyncStorage.getItem("token");

        //-------- For web(testing) use SessionStorage ----------
        const USERID = sessionStorage.getItem("userId");
        const TOKEN = sessionStorage.getItem("token");
        const payload = { alarmTime: finalTime, userId: USERID, token: TOKEN }
        await axios.post('http://192.168.1.181:5000/createAlarm', payload);
    }

    //open up the date picker
    let picker;
    if (pickerOpen) {

        picker =
            <>
                {/* <DatePicker
                    minuteInterval={1}
                    style={styles.datePicker}
                    onTimeChange={(time) => setTime(time)}
                    onDateChange={(date) => setDate(date)}
                /> */}
                <TextInput style={{ color: "#f3f3f3" }} placeholder="setTime" onChange={(e) => setTime(e.target.value)} />
                <TextInput style={{ color: "#f3f3f3" }} placeholder="setDate" onChange={(e) => setDate(e.target.value)} />
                <View style={styles.alarmButtonsContainer}>
                    <TouchableOpacity onPress={() => createAlarm()} style={styles.setAlarmButton}>
                        <Text>Set</Text>
                    </TouchableOpacity>
                    <TouchableOpacity onPress={() => cancelAlarm()} style={styles.setCloseButton}>
                        <Text>Cancel</Text>
                    </TouchableOpacity>
                </View>
            </>
    }
    else picker = null;



    //confirm with user if they want to activate the alarm
    let activateModal;
    if (turnOnNotify) {

        if (active) {
            activateModal =
                <View style={styles.activateModal}>
                    <Text style={{ fontSize: 25 }}>Cancel alarm for {time} on {date}?</Text>
                    <View style={styles.alarmButtonsContainer}>
                        <TouchableOpacity onPress={() => cancelAlarm()} style={styles.setAlarmButton}>
                            <Text>Confirm</Text>
                        </TouchableOpacity>
                        <TouchableOpacity onPress={() => setTurnOnNotify(false)} style={styles.setCloseButton}>
                            <Text>Back</Text>
                        </TouchableOpacity>
                    </View>
                </View>
        }
        else {
            activateModal =
                <View style={styles.activateModal}>
                    <Text style={{ fontSize: 25 }}>Set alarm for {time} on {date}?</Text>
                    <View style={styles.alarmButtonsContainer}>
                        <TouchableOpacity onPress={() => confirmCreateAlarm()} style={styles.setAlarmButton}>
                            <Text>Confirm</Text>
                        </TouchableOpacity>
                        <TouchableOpacity onPress={() => setTurnOnNotify(false)} style={styles.setCloseButton}>
                            <Text>Back</Text>
                        </TouchableOpacity>
                    </View>
                </View>
        }
    }
    else activateModal = null;


    return (
        <View style={styles.container}>
            <View style={styles.topContainer}>
                <TouchableOpacity onPress={storeClick} style={styles.button}>
                    <Icon color="#e1e1e1" name="shopping-outline" size={25} />
                </TouchableOpacity>
                <TouchableOpacity onPress={alarmsClick} style={styles.button} >
                    <Icon color="#e1e1e1" name="clock-outline" size={25} />
                </TouchableOpacity>
                <Coin color="#C7F0DB" name="coins" style={{ marginLeft: 60 }} size={15} />
                <Text style={{ color: '#C7F0DB', marginLeft: 6 }}>+150</Text>
                <TouchableOpacity onPress={profileClick} style={styles.profilebutton}>
                    <Icon color="#e1e1e1" name="account-circle-outline" size={25} />
                </TouchableOpacity>
            </View>
            <View style={styles.mainPage}>
                {picker}
                {activateModal}
                <TouchableOpacity style={styles.alarmContainer} onPress={() => setPickerOpen(true)}>
                    <View style={styles.rowAlarmContainer}>
                        <Text style={styles.alarmTime}>{time == '' ? "00:00" : time}</Text>
                        <TouchableOpacity style={styles.activateButton} onPress={() => turnOn()}>
                            <Text style={{ fontSize: 20, color: '#222' }}>{active ? "active" : "Turn On"}</Text>
                        </TouchableOpacity>
                    </View>
                    <Text style={{ fontSize: 16, marginLeft: 40 }}>{date}</Text>
                </TouchableOpacity>
            </View>
        </View >
    )
}

const styles = StyleSheet.create({
    container: {
        display: 'flex',
        flex: 1,
        flexDirection: 'column',
    },
    topContainer: {
        flex: 1,
        alignItems: 'center',
        display: 'flex',
        flexDirection: 'row',
        height: 40,
        maxHeight: 60,
        width: '100%',
        backgroundColor: '#060930'
    },
    mainPage: {
        backgroundColor: '#060930',
        display: 'flex',
        flex: 1,
        justifyContent: 'flex-start',
        alignItems: 'center',
    },
    button: {
        width: 60,
        height: 40,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 5,
        marginLeft: 20,
        color: '#e1e1e1'
    },
    profilebutton: {
        width: 60,
        height: 40,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
