import React, { useState, useEffect, useContext, useCallback } from 'react';
import { SocketContext } from './socket';
import { TouchableOpacity, StyleSheet, Text, TextInput, View, TouchableOpacityBase } from 'react-native';
import Icon from 'react-native-vector-icons/MaterialCommunityIcons';
import IonIcon from 'react-native-vector-icons/Ionicons';



export default function Chatbox(props) {

    const [dismissOpen, setDismissOpen] = useState(false);
    const [dismissCount, setDismissCount] = useState(0);
    const [snoozeCount, setSnoozeCount] = useState(0);
    const [room, setRoom] = useState('');
    const [alarmId, setAlarmId] = useState('');
    const [disconnected, setDisconnected] = useState(false);
    const [modalOpen, setModalOpen] = useState(true);
    const socket = useContext(SocketContext);
    const [text, setText] = useState('');


    const getMessage = useCallback((message) => {
        console.log('received message: ', message);

    }, []);

    const triggered = useCallback((room, alarmId) => {
        console.log('joining room: ', room);
        setRoom(room);
        setAlarmId(alarmId);
        setModalOpen(true);
    }, []);

    const resetStuff = useCallback(() => {
        setModalOpen(false);
        setDismissOpen(true);
        setSnoozeCount(0);
        setDismissCount(0);
    }, []);

    const disconnection = useCallback(() => {
        setDisconnected(true);
        setModalOpen(false);
    }, [])


    useEffect(() => {

        socket.on('triggered', (room, alarmId) => {
            triggered(room, alarmId);
        });
        socket.on('get-message', (message) => {
            getMessage(message);
        });
        socket.on('get-snooze', () => {
            setSnoozeCount(snoozeCount + 1);  //other user hit snooze
        })
        socket.on('get-dismiss', () => {
            setDismissCount(dismissCount + 1); //other user hit dismiss
        })
        socket.on('snooze-begin', () => {
            console.log('snooze for 9 minutes!');
            resetStuff();
        })
        socket.on('dismiss-begin', () => {
            console.log('both users have dismissed alarm!');
        })
        socket.on('room-disconnect', () => {
            console.log('time up, disconnected from room');
            resetStuff();
            disconnection();
        })
        return () => {
            socket.offAny();
        }
    }, [socket, getMessage, resetStuff, disconnection, triggered])

    function back() {
        props.navigation.navigate("Home");
    }

    function dismiss() {
        setDismissCount(dismissCount + 1);
        socket.emit('send-dismiss', room, alarmId);
        setDismissOpen(false);
    }
    function snooze() {
        setSnoozeCount(snoozeCount + 1);
        socket.emit('send-snooze', room, alarmId);   //tell other user that you hit snooze
        setDismissOpen(false);
    }

    const sendText = useCallback((e) => {
        e.preventDefault();
        socket.emit('send-message', sessionStorage.getItem('username'), text, room);   //when modal is closed this function is called
        addOutTexts(text);
        setText('');

    }, [socket, text]);

    // let dismissButton;
    // if (dismissOpen) {
    //     dismissButton =
    //         <View style={{ display: 'flex', flexDirection: 'row', justifyContent: 'center', position: 'relative', zIndex: 4 }}>
    //             <TouchableOpacity onPress={snooze}>
    //                 <Text>Vote Snooze {`(${snoozeCount}/2)`}</Text>
    //             </TouchableOpacity>
    //             <TouchableOpacity onPress={dismiss}>
    //                 <Text>Dismiss {`(${dismissCount}/2)`}</Text>
    //             </TouchableOpacity>
    //         </View>
    // }

    if (modalOpen) {
        return (
            <View style={styles.container}>
                <View style={styles.topbar}>
                    <TouchableOpacity onPress={back} style={styles.backButton} >
                        <IonIcon color="#e1e1e1" name="arrow-back-sharp" size={25} />
                    </TouchableOpacity>
                </View>
            </View>
        )
    }
    else return null;
}

const styles = StyleSheet.create({
    container: {
        display: 'flex',
        width: '100%',
        height: '100%',
        backgroundColor: '#222'
    },
    topbar: {
        width: '100%',
        height: 60,
        backgroundColor: 'grey',
        justifyContent: 'center',
    },
    backButton: {
        height: 30,
        width: 30,
        marginLeft: 20
    }
})
