import React, { useState, useEffect } from 'react';
import { TouchableOpacity, Button, SafeAreaView, StyleSheet, Text, TextInput, View, TouchableOpacityBase } from 'react-native';
import Icon from 'react-native-vector-icons/MaterialCommunityIcons';
import Coin from 'react-native-vector-icons/FontAwesome5';
import DatePicker from 'react-native-modern-datepicker';

export default function Home(props) {
    const [date, setDate] = useState('');
    const [time, setTime] = useState('');
    const [pickerOpen, setPickerOpen] = useState(false);
    const [active, setActive] = useState(false);
    const [turnOnNotify, setTurnOnNotify] = useState(false);


    function storeClick() {
        props.navigation.navigate('Store');
    }
    function alarmsClick() {
        alert('already in alarms page');
    }
    function profileClick() {
        alert('clicked on profile');
    }

    function createAlarm() {
        setPickerOpen(false);
        setTurnOnNotify(true);

        console.log()
    }
    function cancelAlarm() {
        setActive(false);
        setPickerOpen(false);
        setTurnOnNotify(false);
    }
    function turnOn() {
        setTurnOnNotify(true);
    }

    async function confirmCreateAlarm() {
        setActive(true);
        setPickerOpen(false);
        setTurnOnNotify(false);


        const eventString = `${date} ${time}:00`;
        const event = new Date(eventString);
        event.setHours(event.getHours() - 4);

    }


    //open up the date picker
    let picker;
    if (pickerOpen) {

        picker =
            <>
                <DatePicker
                    minuteInterval={5}
                    style={styles.datePicker}
                    onTimeChange={(time) => setTime(time)}
                    onDateChange={(date) => setDate(date)}
                />
                <View style={styles.alarmButtonsContainer}>
                    <TouchableOpacity onPress={() => createAlarm()} style={styles.setAlarmButton}>
                        <Text>Set</Text>
                    </TouchableOpacity>
                    <TouchableOpacity onPress={() => cancelAlarm()} style={styles.setCloseButton}>
                        <Text>Cancel</Text>
                    </TouchableOpacity>
                </View>
            </>
    }
    else picker = null;



    //confirm with user if they want to activate the alarm
    let activateModal;
    if (turnOnNotify) {

        if (active) {
            activateModal =
                <View style={styles.activateModal}>
                    <Text style={{ fontSize: 25 }}>Cancel alarm for {time} on {date}?</Text>
                    <View style={styles.alarmButtonsContainer}>
                        <TouchableOpacity onPress={() => cancelAlarm()} style={styles.setAlarmButton}>
                            <Text>Confirm</Text>
                        </TouchableOpacity>
                        <TouchableOpacity onPress={() => setTurnOnNotify(false)} style={styles.setCloseButton}>
                            <Text>Back</Text>
                        </TouchableOpacity>
                    </View>
                </View>
        }
        else {
            activateModal =
                <View style={styles.activateModal}>
                    <Text style={{ fontSize: 25 }}>Set alarm for {time} on {date}?</Text>
                    <View style={styles.alarmButtonsContainer}>
                        <TouchableOpacity onPress={() => confirmCreateAlarm()} style={styles.setAlarmButton}>
                            <Text>Confirm</Text>
                        </TouchableOpacity>
                        <TouchableOpacity onPress={() => setTurnOnNotify(false)} style={styles.setCloseButton}>
                            <Text>Back</Text>
                        </TouchableOpacity>
                    </View>
                </View>
        }
    }
    else activateModal = null;


    return (
        <View style={styles.container}>
            <View style={styles.topContainer}>
                <TouchableOpacity onPress={storeClick} style={styles.button}>
                    <Icon color="#e1e1e1" name="shopping-outline" size={25} />
                </TouchableOpacity>
                <TouchableOpacity onPress={alarmsClick} style={styles.button} >
                    <Icon color="#e1e1e1" name="clock-outline" size={25} />
                </TouchableOpacity>
                <Coin color="#C7F0DB" name="coins" style={{ marginLeft: 60 }} size={15} />
                <Text style={{ color: '#C7F0DB', marginLeft: 6 }}>+150</Text>
                <TouchableOpacity onPress={profileClick} style={styles.profilebutton}>
                    <Icon color="#e1e1e1" name="account-circle-outline" size={25} />
                </TouchableOpacity>
            </View>
            <View style={styles.mainPage}>
                {picker}
                {activateModal}
                <TouchableOpacity style={styles.alarmContainer} onPress={() => setPickerOpen(true)}>
                    <View style={styles.rowAlarmContainer}>
                        <Text style={styles.alarmTime}>{time == '' ? "00:00" : time}</Text>
                        <TouchableOpacity style={styles.activateButton} onPress={() => turnOn()}>
                            <Text style={{ fontSize: 20, color: '#222' }}>{active ? "active" : "Turn On"}</Text>
                        </TouchableOpacity>
                    </View>
                    <Text style={{ fontSize: 16, marginLeft: 40 }}>{date}</Text>
                </TouchableOpacity>
            </View>
        </View >
    )
}

const styles = StyleSheet.create({
    container: {
        display: 'flex',
        flex: 1,
        flexDirection: 'column',
    },
    topContainer: {
        flex: 1,
        alignItems: 'center',
        display: 'flex',
        flexDirection: 'row',
        height: 40,
        maxHeight: 60,
        width: '100%',
        backgroundColor: '#060930'
    },
    mainPage: {
        backgroundColor: '#060930',
        display: 'flex',
        flex: 1,
        justifyContent: 'flex-start',
        alignItems: 'center',
    },
    button: {
        width: 60,
        height: 40,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 5,
        marginLeft: 20,
        color: '#e1e1e1'
    },
    profilebutton: {
        width: 60,
        height: 40,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 5,
        marginLeft: 20,
        color: '#e1e1e1'
    },
    buttonText: {
        fontSize: 15,
        color: '#e1e1e1'
    },
    datePicker: {
        borderRadius: 10,
        position: 'relative',
        zIndex: 4,
    },
    alarmContainer: {
        display: 'flex',
        flex: 1,
        height: 120,
        maxHeight: 120,
        width: "90%",
        backgroundColor: '#fff',
        marginTop: 40,
        borderRadius: 10,
        position: 'absolute',
        zIndex: 1,
        justifyContent: 'center',
    },
    pickerView: {

        width: '90%',
        height: 100
    },
    setAlarmButton: {
        width: '45%',
        height: 70,
        backgroundColor: 'skyblue',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 10,
    },
    setCloseButton: {
        width: '45%',
        marginLeft: 10,
        height: 70,
        backgroundColor: '#fff',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 10,
    },
    alarmButtonsContainer: {
        marginTop: 5,
        display: 'flex',
        flexDirection: 'row',
        flex: 1
    },
    alarmTime: {
        fontSize: 60,
        alignSelf: 'flex-start',
        marginLeft: 20
    },
    rowAlarmContainer: {
        width: '100%',
        height: 80,
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'center'
    },
    activateButton: {
        marginLeft: 30,
        width: 100,
        height: 60,
        borderRadius: 10,
        backgroundColor: 'skyblue',
        alignItems: 'center',
        justifyContent: 'center'
    },
    activateModal: {
        position: 'relative',
        zIndex: 5,
        marginTop: 40,
        display: 'flex',
        flex: 1,
        flexDirection: 'column',
        width: '90%',
        height: 160,
        maxHeight: 160,
        backgroundColor: '#fff',
        justifyContent: 'center',
        alignItems: 'center'
    }
})
