import React, { useState, useEffect, useContext, useCallback } from 'react';
import { TouchableOpacity, StyleSheet, Text, TextInput, View } from 'react-native';
import { SocketContext } from './socket';

const socket = useContext(SocketContext);

export default function AlarmTriggeredModal(props) {
    const [snoozeCount, setSnoozeCount] = useState(0);
    const [dismissCount, setDismissCount] = useState(0);
    const [partner, setPartner] = useState('Rainbow69');
    const [timeLeft, setTimeLeft] = useState(10);

    const room = props.room;
    const alarmId = props.alarmId;

    useEffect(() => {
        socket.on('get-snooze', () => {
            setSnoozeCount(snoozeCount + 1);
        })
        socket.on('get-dismiss', () => {
            setDismissCount(dismissCount + 1);
        })
        socket.on('snooze-begin', () => {
            resetStuff();
        })
        socket.on('dismiss-begin', () => {
            resetStuff();
        })
    }, [socket])

    function resetStuff() {
        props.onSetAlarmTriggered();
        setTimeLeft(10);
        setDismissCount(0);
        setSnoozeCount(0);
    }

    // function timerEnd(){
    //     alert('time is up');
    // }
    // function decrement() {
    //     if (timeLeft > 0) setTimeLeft(timeLeft - 1);
    //     else timesUp();
    // }
    // setInterval(decrement, 1000);



    function snooze() {
        setSnoozeCount(snoozeCount + 1);
        socket.emit('send-snooze', room, alarmId);
    }
    function dismiss() {
        setDismissCount(dismissCount + 1);
        socket.emit('send-dismiss', room, alarmId);

    }
    function mute() {
        props.onMute;
    }

    function timesUp() {
        props.onSetAlarmTriggered();
    }

    return (
        <View style={styles.container}>
            <View style={styles.topbar}>
                <Text style={{ fontSize: 15 }}>You and {partner}</Text>
                <Text style={{ fontSize: 20, marginLeft: 60 }}>00:{timeLeft}</Text>
            </View>
            <View style={styles.voteButtonContainer}>
                <TouchableOpacity onPress={snooze} style={styles.snoozeButton}>
                    <Text style={{ fontSize: 25 }}>Vote Snooze</Text>
                    <Text style={{ fontSize: 25 }}>{snoozeCount}/2</Text>
                </TouchableOpacity>
                <TouchableOpacity onPress={dismiss} style={styles.dismissButton}>
                    <Text style={{ fontSize: 25 }}>Vote Get Up</Text>
                    <Text style={{ fontSize: 25 }}>{dismissCount}/2</Text>
                </TouchableOpacity>
            </View>
            <TouchableOpacity onPress={mute} style={styles.muteButton}>
                <Text style={{ fontSize: 35 }}>Mute Audio</Text>
            </TouchableOpacity>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        display: 'flex',
        width: '90%',
        height: '50%',
        position: 'relative',
        zIndex: 5,
        borderRadius: 15,
        marginTop: 30,
    },
    topbar: {
        width: '100%',
        height: 40,
        backgroundColor: 'white',
        borderTopLeftRadius: 15,
        borderTopRightRadius: 15,
        borderBottomWidth: 1,
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'flex-start',
        alignItems: 'center',
        padding: 10
    },
    voteButtonContainer: {
        display: 'flex',
        flexDirection: 'row',
        width: '100%',
        height: '50%',
        backgroundColor: 'white'
    },
    snoozeButton: {
        width: '50%',
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: 'white'
    },
    dismissButton: {
        width: '50%',
        height: '100%',
        backgroundColor: 'white',
        justifyContent: 'center',
        alignItems: 'center',
        borderLeftWidth: 1
    },
    muteButton: {
        width: '100%',
        height: '35%',
        backgroundColor: 'white',
        borderBottomLeftRadius: 15,
        borderBottomRightRadius: 15,
        justifyContent: 'center',
        alignItems: 'center',
        borderTopColor: '#222',
        borderTopWidth: 1
    }

})
